theorem population_growth_problem 
  (i j : ℝ)  -- percentages of population growth
  (h_valid_percentages : i ≥ 0 ∧ j ≥ 0) : 
  let initial_population := 100  -- starting population normalized to 100
  let population_after_first_increase := initial_population * (1 + i/100)
  let final_population := population_after_first_increase * (1 + j/100)
  let total_increase_percentage := 
    ((final_population - initial_population) / initial_population) * 100
  total_increase_percentage = i + j + (i * j / 100) := by

