theorem odd_sum_theorem (n : ℕ) : 
  let S := Finset.sum (Finset.range n) (fun k => 2 * (k + 1) - 1)
  S = n * (n + 2) := by

theorem logarithm_problem 
  (x : ℝ) 
  (hx_domain : -1 < x ∧ x < 1) :
  let f := fun (t : ℝ) => Real.log ((1 + t) / (1 - t))
  let expr := (3*x + x^3) / (1 + 3*x^2)
  f expr = 3 * f x := by

theorem remainder_problem : 
  let remainder := (x^13 + 1) % (x - 1)
  remainder = 2 := by

